package com.ajou_nice.with_pet.withpet_service.model.entity;


import com.ajou_nice.with_pet.admin.model.dto.AddWithPetServiceRequest;
import com.ajou_nice.with_pet.admin.model.dto.AddWithPetServiceRequest.WithPetServiceModifyRequest;
import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.Lob;
import javax.persistence.Table;

import com.ajou_nice.with_pet.admin.model.dto.UpdateWithPetServiceRequest;
import lombok.AllArgsConstructor;
import lombok.Builder;
import lombok.Getter;
import lombok.NoArgsConstructor;
import lombok.ToString;

//Service는 위드펫에서 기본적으로 제공하는 Service들을 말한다.
@Entity
@Getter
@AllArgsConstructor
@NoArgsConstructor
@Builder
@ToString
@Table(name = "withpetservice")
public class WithPetService {

	@Id @GeneratedValue(strategy = GenerationType.IDENTITY)
	@Column(name = "service_id")
	private Long id;

	private String serviceName;

	@Lob
	private String serviceImg;

	private String introduction;

	public void updateServiceInfo(UpdateWithPetServiceRequest withPetServiceRequest){
		this.serviceName = withPetServiceRequest.getServiceName();
		this.serviceImg = withPetServiceRequest.getServiceImg();
		this.introduction = withPetServiceRequest.getServiceIntroduction();
	}

	public static WithPetService toEntity(AddWithPetServiceRequest addWithPetServiceRequest){

		String img = addWithPetServiceRequest.getServiceImg();
		if(addWithPetServiceRequest.getServiceImg() == null || addWithPetServiceRequest.getServiceImg().isEmpty()){
			img = "data:image/png;base64,";

		}
		return WithPetService.builder()
				.serviceName(addWithPetServiceRequest.getServiceName())
				.serviceImg(img)
				.introduction(addWithPetServiceRequest.getServiceIntro())
				.build();
	}

}
